require.config({"config": {
        "jsbuild":{"Dotdigitalgroup_Email/js/emailCaptureLogin.js":"define(['jquery', 'domReady!'], function ($) {\n    'use strict';\n\n    var previousEmail;\n\n    /**\n     * Email validation\n     * @param {String} sEmail\n     * @returns {Boolean}\n     */\n    function validateEmail(sEmail) {\n        return /^([+\\w-\\.]+)@((\\[[0-9]{1,3}\\.[0-9]{1,3}\\.[0-9]{1,3}\\.)|(([\\w-]+\\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\\]?)$/\n            .test(sEmail);\n    }\n\n    /**\n     * Send captured email\n     *\n     * @param selectors\n     * @param url\n     */\n    function emailCapture(selectors) {\n        $(document).on('blur', selectors.join(', '), function() {\n            var email = $(this).val();\n            if (!email || email === previousEmail || !validateEmail(email)) {\n                return;\n            }\n\n            if (typeof window.dmPt !== 'undefined') {\n                window.dmPt('identify', email);\n            }\n        });\n    }\n\n    /**\n     * Exported/return email capture\n     * @param {Object} config\n     */\n    return function () {\n        let selectors = [];\n        selectors.push('input[id=\"email\"]');\n        emailCapture(selectors);\n    };\n});\n"}
}});
